    @Override
    public int prices(Object value) {
        MoveOption moves = (MoveOption) value;
        int price = 0;
        for (int i = 0; i < MoveOption.MOVES_NUM; i++)
            if (moves.has(i))
                price += getPriceOf(ConfigKeyConstants.CHANGE_MOVES, 10000);
        return price;
    }

    @Override
    protected void priceSummaries() {
        MoveConfig config = FusionPixelmon.getInstance().getConfiguration().getMoveConfig();
        addPriceSummary(config.MovePriceSummary(), getPriceOf(ConfigKeyConstants.CHANGE_MOVES, 10000) + " "+ config.MoveEach());
    }

